import { Pagination, PaginationContent, PaginationEllipsis, PaginationItem, PaginationLink, PaginationNext, PaginationPrevious } from './pagination';

function MyPaginatedList({ currentPage, totalPages, onPageChange }) {
  const pages = Array.from({ length: totalPages }, (_, i) => i + 1);

  return (
    <Pagination>
      <PaginationContent>
        <PaginationItem>
          <PaginationPrevious onClick={() => onPageChange(currentPage - 1)} disabled={currentPage === 1} />
        </PaginationItem>
        {pages.map((page) => (
          <PaginationItem key={page}>
            <PaginationLink
              onClick={() => onPageChange(page)}
              isActive={currentPage === page}
            >
              {page}
            </PaginationLink>
          </PaginationItem>
        ))}
        <PaginationItem>
          <PaginationNext onClick={() => onPageChange(currentPage + 1)} disabled={currentPage === totalPages} />
        </PaginationItem>
      </PaginationContent>
    </Pagination>
  );
}

import { Pagination, PaginationContent, PaginationItem, PaginationLink, PaginationPrevious, PaginationNext } from "@repo/web/components/ui/pagination";

function MyPaginatedContent() {
  const currentPage = 3;
  const totalPages = 10;

  return (
    <Pagination>
      <PaginationContent>
        <PaginationItem>
          <PaginationPrevious href={currentPage > 1 ? `/items?page=${currentPage - 1}` : undefined} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink href="/items?page=1" isActive={currentPage === 1}>1</PaginationLink>
        </PaginationItem>
        {currentPage > 3 && <PaginationItem>...</PaginationItem>}
        {currentPage > 2 && <PaginationItem><PaginationLink href="/items?page=2">2</PaginationLink></PaginationItem>}
        <PaginationItem>
          <PaginationLink href={`/items?page=${currentPage}`} isActive>{currentPage}</PaginationLink>
        </PaginationItem>
        {currentPage < totalPages - 1 && <PaginationItem><PaginationLink href={`/items?page=${currentPage + 1}`}>{currentPage + 1}</PaginationLink></PaginationItem>}
        {currentPage < totalPages - 2 && <PaginationItem>...</PaginationItem>}
        <PaginationItem>
          <PaginationLink href={`/items?page=${totalPages}`} isActive={currentPage === totalPages}>{totalPages}</PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationNext href={currentPage < totalPages ? `/items?page=${currentPage + 1}` : undefined} />
        </PaginationItem>
      </PaginationContent>
    </Pagination>
  );
}

import { Pagination, PaginationContent, PaginationItem, PaginationLink, PaginationNext, PaginationPrevious } from './pagination';

function MyPaginatedList({ currentPage, totalPages }) {
  const hasNextPage = currentPage < totalPages;
  const nextPageUrl = `/items?page=${currentPage + 1}`;

  return (
    <Pagination>
      <PaginationContent>
        <PaginationItem>
          <PaginationPrevious href={currentPage > 1 ? `/items?page=${currentPage - 1}` : '#'} aria-disabled={currentPage === 1} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink href="#" isActive>{currentPage}</PaginationLink>
        </PaginationItem>
        {hasNextPage && (
          <PaginationItem>
            <PaginationNext href={nextPageUrl} />
          </PaginationItem>
        )}
      </PaginationContent>
    </Pagination>
  );
}

import { Pagination, PaginationContent, PaginationItem, PaginationLink, PaginationEllipsis } from '@repo/web/components/ui/pagination';

