// This component illustrates how ChartContextProps is consumed internally within the library
function CustomMetricTooltipContent({
  active,
  payload,
  label
}: TooltipProps<number, string>) {
  const { config } = useChart(); // Accessing ChartConfig via ChartContextProps

  if (!active || !payload || !payload.length) return null;

  const item = payload[0];
  const key = item.dataKey as string;
  const itemConfig = config[key]; // Using the config from ChartContextProps

  return (
    <div className="grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl">
      <p className="font-medium">Date: {label}</p>
      <p className="text-muted-foreground">{itemConfig?.label || key}: <span className="font-medium text-foreground tabular-nums">{item.value}</span></p>
      {itemConfig?.icon && <itemConfig.icon />}
    </div>
  );
}

