import { useChart, ChartTooltipContent } from '@repo/web/components/ui/chart';
import * as Recharts from 'recharts';

function CustomMetricTooltip() {
  // The useChart hook retrieves the config from ChartContext
  const { config } = useChart();

  // Now 'config' can be used to render custom tooltip content
  return (
    <ChartTooltipContent
      formatter={(value, name) => {
        const itemConfig = config[name as keyof typeof config]; // Access config for specific data key
        return itemConfig?.label ? `${itemConfig.label}: ${value}` : String(value);
      }}
    />
  );
}

