// Inside a function that sets the sidebar state
function setSidebarState(isExpanded: boolean) {
  document.cookie = `${SIDEBAR_COOKIE_NAME}=${isExpanded}; path=/; max-age=${SIDEBAR_COOKIE_MAX_AGE}; SameSite=Lax`;
}

