import { Table, TableBody, TableCell, TableFooter, TableHead, TableHeader, TableRow } from './table';

function ProductSummaryTable() {
  const products = [
    { name: 'Laptop', price: 1200, quantity: 1 },
    { name: 'Mouse', price: 25, quantity: 2 }
  ];
  const totalItems = products.reduce((sum, p) => sum + p.quantity, 0);
  const totalPrice = products.reduce((sum, p) => sum + (p.price * p.quantity), 0);

  return (
    <Table>
      <TableHeader>
        <TableRow>
          <TableHead>Product</TableHead>
          <TableHead>Quantity</TableHead>
          <TableHead className="text-right">Price</TableHead>
        </TableRow>
      </TableHeader>
      <TableBody>
        {products.map((product, index) => (
          <TableRow key={index}>
            <TableCell>{product.name}</TableCell>
            <TableCell>{product.quantity}</TableCell>
            <TableCell className="text-right">${product.price.toFixed(2)}</TableCell>
          </TableRow>
        ))}
      </TableBody>
      <TableFooter>
        <TableRow>
          <TableCell colSpan={2}>Total Items</TableCell>
          <TableCell className="text-right font-bold">{totalItems}</TableCell>
        </TableRow>
        <TableRow>
          <TableCell colSpan={2}>Grand Total</TableCell>
          <TableCell className="text-right font-bold">${totalPrice.toFixed(2)}</TableCell>
        </TableRow>
      </TableFooter>
    </Table>
  );
}

