import React from 'react';
import { Collapsible, CollapsibleTrigger, CollapsibleContent } from './collapsible';

function AnimatedCollapsibleExample() {
  const [isOpen, setIsOpen] = React.useState(false);

  return (
    <Collapsible open={isOpen} onOpenChange={setIsOpen}>
      <CollapsibleTrigger>
        Click me for animated content
      </CollapsibleTrigger>
      {/* 
        forceMount ensures content is always in DOM, allowing CSS transitions 
        on 'data-state="open"' or 'data-state="closed"' attributes. 
        Actual CSS transitions would be defined in a stylesheet.
      */}
      <CollapsibleContent forceMount>
        <div 
          style={{
            padding: '10px',
            border: '1px solid #ccc',
            marginTop: '5px',
            overflow: 'hidden',
            transition: 'height 0.3s ease-out, opacity 0.3s ease-out'
          }}
          data-state={isOpen ? "open" : "closed"}
        >
          <p>This content will animate when opening/closing.</p>
          <p>The `forceMount` prop keeps it in the DOM.</p>
        </div>
      </CollapsibleContent>
    </Collapsible>
  );
}

