import React from 'react';
import { Carousel, CarouselContent, CarouselItem, type CarouselApi } from './carousel';

function CustomCarouselControls() {
  const [carouselApi, setCarouselApi] = React.useState<CarouselApi | null>(null);

  React.useEffect(() => {
    if (!carouselApi) return;

    console.log('Carousel API is ready!');

    // Example: Programmatically scroll to the second slide after 2 seconds
    const timeout = setTimeout(() => {
      carouselApi.scrollTo(1);
    }, 2000);

    // Example: Listen for slide selection changes
    carouselApi.on('select', () => {
      console.log('Slide changed! Current index:', carouselApi.selectedScrollSnap());
    });

    return () => {
      // Clean up event listeners on component unmount
      carouselApi.off('select');
      clearTimeout(timeout);
    };
  }, [carouselApi]);

  return (
    <Carousel setApi={setCarouselApi}>
      <CarouselContent>
        <CarouselItem>Slide One</CarouselItem>
        <CarouselItem>Slide Two</CarouselItem>
        <CarouselItem>Slide Three</CarouselItem>
      </CarouselContent>
      {/* Custom navigation buttons could use carouselApi.scrollPrev() / scrollNext() here */}
    </Carousel>
  );
}

