import * as React from "react";
import { Carousel, CarouselContent, CarouselItem, useCarousel } from "./carousel";

function CarouselDotIndicators() {
  const { api } = useCarousel();
  const [selectedIndex, setSelectedIndex] = React.useState(0);
  const [scrollSnaps, setScrollSnaps] = React.useState<number[]>([]);

  const scrollTo = React.useCallback((index: number) => {
    api?.scrollTo(index);
  }, [api]);

  React.useEffect(() => {
    if (!api) return;
    setScrollSnaps(api.scrollSnapList());
    api.on("select", () => setSelectedIndex(api.selectedScrollSnap()));
    api.on("reInit", () => setScrollSnaps(api.scrollSnapList()));

    return () => {
      api?.off("select", () => setSelectedIndex(api.selectedScrollSnap()));
      api?.off("reInit", () => setScrollSnaps(api.scrollSnapList()));
    };
  }, [api]);

  return (
    <div style={{ display: "flex", gap: "8px", justifyContent: "center", marginTop: "16px" }}>
      {scrollSnaps.map((_, index) => (
        <button
          key={index}
          onClick={() => scrollTo(index)}
          style={{
            width: "10px",
            height: "10px",
            borderRadius: "50%",
            border: "none",
            backgroundColor: index === selectedIndex ? "purple" : "#ddd",
            cursor: "pointer"
          }}
          aria-label={`Go to slide ${index + 1}`}
        />
      ))}
    </div>
  );
}

